import {useState} from 'react';

function Square({value, onSquareClick}){
    return <button className="square" onClick = {onSquareClick}>{value}</button>;
}
export default function Grid(){

    const[squares, setSquares] = useState(Array(42).fill(null));
    return (
        <>
        {(() => {
            const rows = [];
            const numberOfRows = 6; 
            const numberOfSquaresPerRow = 7; 

            for (let rowIndex = 0; rowIndex < numberOfRows; rowIndex++) {
                const squares = [];
                for (let squareIndex = 0; squareIndex < numberOfSquaresPerRow; squareIndex++) {
                    const index = rowIndex * numberOfSquaresPerRow + squareIndex;
                    squares.push(
                        <Square
                            key={index}
                            value={squares[index]}
                            onSquareClick={() => handleClick(index)}
                        />
                    );
                }
                rows.push(
                    <div key={rowIndex} className="grid-row">
                        {squares}
                    </div>
                );
            }

            return rows;
        })()}
    </>
    );
}
